CREATE TABLE crimes(
   INCIDENT_NUMBER     VARCHAR(13) NOT NULL PRIMARY KEY
  ,OFFENSE_CODE        INTEGER  NOT NULL
  ,OFFENSE_CODE_GROUP  VARCHAR(41)
  ,OFFENSE_DESCRIPTION VARCHAR(58) NOT NULL
  ,DISTRICT            VARCHAR(3)
  ,REPORTING_AREA      INTEGER 
  ,SHOOTING            VARCHAR(1)
  ,OCCURRED_ON_DATE    VARCHAR(19) NOT NULL
  ,YEAR                INTEGER  NOT NULL
  ,MONTH               INTEGER  NOT NULL
  ,DAY_OF_WEEK         VARCHAR(9) NOT NULL
  ,HOUR                INTEGER  NOT NULL
  ,UCR_PART            VARCHAR(10)
  ,STREET              VARCHAR(25)
  ,Lat                 VARCHAR(20)
  ,Long                VARCHAR(20)
  ,Location            VARCHAR(27) NOT NULL
);
